import React, { useState, useEffect } from 'react';
import { Link, Navigate, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Card from '../components/Card';


function HomePage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if the token exists in local storage
    const token = localStorage.getItem('authToken');
    //console.log(token)
    if (token) {
      navigate('/');
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
    }
  }, []);

  if (!isLoggedIn) {
    // Redirect to login page if not logged in
    return <Navigate to="/login" />;
  }

   // Dummy product data
   const products = [
    { id: 1, name: 'Product 1', image: '/images.jpeg', category: 'Category 1' },
    { id: 2, name: 'Product 2', image: '/images.jpeg', category: 'Category 2' },
    { id: 3, name: 'Product 3', image: '/images.jpeg', category: 'Category 1' },
    { id: 4, name: 'Product 4', image: '/images.jpeg', category: 'Category 3' },
    { id: 5, name: 'Product 5', image: '/images.jpeg', category: 'Category 2' }
  ];

  // Get unique categories
  const categories = [...new Set(products.map(product => product.category))];

  return (
    <div>
      <Header />

      <div className="container mx-auto mt-20">
        {/* Category section */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold mb-4">Categories</h2>
          <div className="flex">
            {categories.map(category => (
              <div key={category} className="mr-4">
                <button className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded">{category}</button>
              </div>
            ))}
          </div>
        </div>

        {/* Product cards */}
        <div className="grid grid-cols-3 gap-8">
          {products.map(product => (
            <Card key={product.id} product={product} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default HomePage;
