import React, { useState, useEffect } from 'react';
import { Link, Navigate } from 'react-router-dom';

function HomePage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check if the token exists in local storage
    const token = localStorage.getItem('authToken');
    if (token) {
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken'); // Remove token from local storage
    setIsLoggedIn(false);
  };

  if (!isLoggedIn) {
    // Redirect to login page if not logged in
    return <Navigate to="/login" />;
  }

  return (
    <div>
      <header style={headerStyle}>
        <div style={logoStyle}>Logo</div>
        <div style={titleStyle}>can-pare</div>
        <div style={loginStyle}>
          {isLoggedIn ? (
            <button onClick={handleLogout}>Log out</button>
          ) : (
            <Link to="/login"><button>Log in</button></Link>
          )}
        </div>
      </header>
      <div style={containerStyle}>
        <div style={categoryStyle}>Category</div>
        <div style={productsStyle}>
          {[...Array(5)].map((_, i) => (
            <div style={productCardStyle} key={i}>
              <div style={productImageStyle}>Image</div>
              <div>Product {i + 1}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

const headerStyle = {
  height: '80px',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
  padding: '0 20px',
  position: 'fixed',
  width: '100%',
  top: 0,
  backgroundColor: '#fff',
  borderBottom: '1px solid #ccc'
};

const logoStyle = { flex: 1 };
const titleStyle = { flex: 2, textAlign: 'center' };
const loginStyle = { flex: 1, textAlign: 'right', marginRight: '20px' };
const containerStyle = { display: 'flex', marginTop: '100px', height: '1420px' };
const categoryStyle = { width: '25%', borderRight: '1px solid #ccc' };
const productsStyle = { width: '50%', display: 'grid', gridTemplateColumns: '1fr', gap: '20px' };
const productCardStyle = { display: 'flex', alignItems: 'center', border: '1px solid #ccc', padding: '10px' };
const productImageStyle = { width: '25px', height: '25px', backgroundColor: '#ddd', marginRight: '20px' };

export default HomePage;
