import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/users/login', { email, password });
      // Handle successful login
      console.log(response.data.token)
      localStorage.setItem('authToken', response.data.token); // Store authentication token
      setIsLoggedIn(true); // Update state to reflect successful login
      navigate('/');

    } catch (err) {
      setError('Invalid email or password');
    }
  };

  if (isLoggedIn) {
    // Redirect to main page if user is logged in
    navigate('/');
  }

  return (
    <div>
      <Header/>
      <div style={formContainerStyle}>
        <form onSubmit={handleLogin} style={formStyle}>
          <h2>Login</h2>
          <div>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {error && <div style={errorStyle}>{error}</div>}
          <div>
            <button type="submit">Login</button>
          </div>
          <div>
            Don't have an account? <Link to="/register">Register</Link>
          </div>
        </form>
      </div>
    </div>
  );
}

const headerStyle = {
  height: '80px',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  padding: '0 20px',
  position: 'fixed',
  width: '100%',
  top: 0,
  backgroundColor: '#fff',
  borderBottom: '1px solid #ccc'
};

const logoStyle = { flex: 1 };
const titleStyle = { flex: 2, textAlign: 'center' };
const formContainerStyle = { display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100vh' };
const formStyle = { display: 'flex', flexDirection: 'column', width: '300px', padding: '20px', border: '1px solid #ccc' };
const errorStyle = { color: 'red' };

export default LoginPage;
