import React, { useState, useEffect } from 'react';
import { Link, Navigate, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Card from '../components/Card';


function HomePage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if the token exists in local storage
    const token = localStorage.getItem('authToken');
    //console.log(token)
    if (token) {
      navigate('/');
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
    }
  }, []);

  if (!isLoggedIn) {
    // Redirect to login page if not logged in
    return <Navigate to="/login" />;
  }

  // Dummy product data
  const products = [
    { id: 1, name: 'Product 1', image: '/images.jpeg' },
    { id: 2, name: 'Product 2', image: '/images.jpeg' },
    { id: 3, name: 'Product 3', image: '/images.jpeg' },
    { id: 4, name: 'Product 4', image: '/images.jpeg' },
    { id: 5, name: 'Product 5', image: '/images.jpeg' }
  ];

  return (
    <div>
      <Header />

      <div className="container mx-auto mt-20 grid grid-cols-3 gap-8">
        {products.map(product => (
          <Card key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
}

export default HomePage;
