import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';

function RegisterPage() {
  const [name, setName] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [surname, setSurname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/users/register', { name, surname, email, password });
      // Handle successful registration
      if (response.status === 201) {
        localStorage.setItem('authToken', response.data.token); // Store authentication token
        navigate('/'); // Redirect to home page after successful registration
      }
    } catch (err) {
      setError('Registration failed');
    }
  };

  return (
    <div>
      <Header isLoggedIn={isLoggedIn}/>
      <div style={formContainerStyle}>
        <form onSubmit={handleRegister} style={formStyle}>
          <h2 className='font-bold text-violet-600	'>Register</h2>
          <div>
            <input
              type="text"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div>
            <input
              type="text"
              placeholder="Surname"
              value={surname}
              onChange={(e) => setSurname(e.target.value)}
              required
            />
          </div>
          <div>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div>
            <input
              type="password"
              placeholder="Confirm Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
          </div>
          {error && <div style={errorStyle}>{error}</div>}
          <div>
            <button type="submit">Register</button>
          </div>
          <div>
            Already have an account? <Link to="/login">Login</Link>
          </div>
        </form>
      </div>
    </div>
  );
}


const formContainerStyle = { display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100vh' };
const formStyle = { display: 'flex', flexDirection: 'column', width: '300px', padding: '20px', border: '1px solid #ccc' };
const errorStyle = { color: 'red' };

export default RegisterPage;
